mapper.update:
update board
set title=#{title}, overview=#{overview}, modified_time=now()
where board_id=#{board_id}

update board
set hit = hit + 1
where board_id=#{board_id}

mapper.select:
select b.board_id, b.title, b.userid, b.regist_time, b.modified_time, b.hit, u.username from board b left join user u on b.userid = u.userid where
b.title like CONCAT("%", #{title}, "%")
u.username = #{username}
