mapper.update:
update hotplace
set title=#{title}, overview=#{overview}, modified_time=now(), image={image}, sido_code=#{sido_code}, gugun_code=#{gugun_code}
where hotplace_id=#{hotplace_id}

update hotplace
set hit = hit + 1
where hotplace_id=#{hotplace_id}

update hotplace
set like = like + 1
where hotplace_id=#{hotplace_id}

mapper.select:
select hotplace_id, image, title
from hotplace
where userId = {userId}
