mapper.select:
select userId, username, refresh_token
from user
where userId = #{userId} and password = #{password}

mapper.update:
update user
set refresh_token = #{token}
where userId = #{userId}

update user
set refresh_token = #{token, jdbcType=VARCHAR}
where userId = #{userId}

mapper.select:
select refresh_token
from user
where userId = #{userId}

mapper.update:
 		update user
		set username=#{username}, email=#{email}, sido_code=#{sido_code}, gugun_code=#{gugun_code}
		where userId=#{userId}

mapper.select:
select password
from user
where userId=#{userId}
