mapper.select:
select userId, username, refresh_token
from user
where userId = #{userId} and password = #{password}

mapper.update:
update user
set refresh_token = #{refresh_token}
where userId = #{userId}

update user
set refresh_token = #{refresh_token, jdbcType=VARCHAR}
where userId = #{userId}

mapper.select:
select refresh_token
from user
where userId = #{userId}
